mapper.select:

SELECT ShopId as Id, COUNT(1) as Result
FROM order_record
WHERE AddTime > #{startTime} AND AddTime < #{endTime}
GROUP BY ShopId
ORDER BY COUNT(1) DESC
LIMIT #{limit}


SELECT ShopId as Id, SUM(Price) as Result
FROM order_record
WHERE AddTime > #{startTime} AND AddTime < #{endTime}
GROUP BY ShopId
ORDER BY SUM(Price) DESC
LIMIT #{limit}

